import React from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import './App.css';
import './project_preview.css';
import classnames from 'classnames';

class ProjectSection extends React.Component {

  renderImg(){
    if (React.Children.toArray(this.props.children)[React.Children.count(this.props.children)-1].type == "ProjectImg") {
      return (
        React.Children.toArray(this.props.children)[React.Children.count(this.props.children)-1]
      );
    }
  }

  render() {
      return (
        <div className="project-section">
          <div className="section-headers">
            <p>{this.props.header}</p>
          </div>
          <div className="section-content">
            <h3 className="section-subheader">{this.props.subheader}</h3>
            <p>{this.props.children}</p>
          </div>
          <p>{this.renderImg}</p>
        </div>
      );
  }
}

export default ProjectSection
