import React from 'react';

class Nav2 extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      hidden: true,
    }
  }

  handleMouseEnter() {
    this.setState({ hidden: false })
  }

  handleMouseLeave() {
    this.setState({ hidden: true })
  }

  render() {
    const { hidden } = this.state;

    return (
      <div className="table-of-contents" onMouseEnter={this.handleMouseEnter}>
        { this.props.items.map(item => {
          return (
            <div className={`${hidden ? 'toc-item' : ''}`}>
              <p class="toc-name hidden">{ item }</p>
            </div>
          )
        }) }
      </div>
    )
  }
};

export default Nav2
