import React from "react";
import './App.css';
import './project_preview.css';
import classnames from 'classnames';

class ProjectPreview extends React.Component {

  handleHover() {

  }

  render() {
      const { imgUrl } = this.props;
      const style = {
        backgroundImage: "url("+this.props.imgUrl+")",
      }

      return (
        <div className={classnames('project_preview', this.props.side)}>
          <div style={style} className="project-preview-img" onmouseover={this.handleHover}></div>
          <h1 className="project-preview-title">{this.props.name} </h1>
          <h2 className="project-preview-description"> - {this.props.description}</h2>
        </div>
      );
  }
}

export default ProjectPreview
